mapper.insert:

  SELECT SEQ_ATTACHED_FILE.NEXTVAL FROM DUAL

INSERT INTO attached_file (
  file_id
  , chat_id
  , file_url
  , file_type
  , file_name
  , file_volume
  , filename_extension
  , writer
  , write_dt
  , modifier
  , modified_dt
  , is_deleted
)
VALUES (
  #{s3UploadFile.fileId}
  , #{chatId}
  , #{s3UploadFile.fileUrl}
  , #{s3UploadFile.fileType}
  , #{s3UploadFile.fileName}
  , #{s3UploadFile.fileSize}
  , #{s3UploadFile.fileExtension}
  , #{employeeId}
  , SYSDATE
  , #{employeeId}
  , SYSDATE
  , 0
)

mapper.select:
SELECT * FROM ( SELECT sub.*, ROWNUM rnum FROM ( SELECT af.chat_id , af.file_id , af.file_name , TO_CHAR(af.write_dt, 'YYYY-MM-DD HH24:MI:SS') AS writeDt , e.name AS sender , t.tags AS tags FROM attached_file af JOIN chat c ON af.chat_id = c.chat_id JOIN employee e ON af.writer = e.employee_id LEFT JOIN ( SELECT file_id , LISTAGG(name, ',') WITHIN GROUP (ORDER BY name) AS tags FROM tag WHERE is_deleted = 0 GROUP BY file_id ) t ON af.file_id = t.file_id WHERE c.chat_room_id = #{chatRoomId} AND af.is_deleted = 0
AND (
af.file_name LIKE '%' || #{tag} || '%'
)
AND af.file_id IN ( SELECT file_id FROM tag WHERE name IN
#{tag}
AND is_deleted = 0 GROUP BY file_id )
ORDER BY af.write_dt DESC ) sub
WHERE ROWNUM <= #{limit} + #{offset} ) WHERE rnum > #{offset}
